mapper.select:
select * from (
	select rownum rnum, no, title, writer, writeDate, hit
	from(
		select no, title, writer, writeDate, hit
		from board
		order by no desc
		)
	)
where rnum between #{startRow} and #{endRow}

select no, title, content, writer, writedate, hit , pw
from board
where no = #{no}

mapper.update:
update board set
	title = #{title},
	content = #{content},
	writer = #{writer}
where no = #{no}
